import { useState, useEffect } from "react";
import React from "react";
import { RxAvatar } from "react-icons/all";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faCheckCircle,
  faFilePen,
  faXmark,
} from "@fortawesome/free-solid-svg-icons";
export const Users = () => {
  const [users, setUsers] = useState([]);
  const handleEnable = (id, bool) => {
    fetch(
      "http://localhost:8082/ShoppingCartAdmin/users/" +
        id +
        "/enabled/" +
        bool,
      {
        method: "GET",
        headers: { "Content-Type": "application/json" },
      }
    ).then(() => {
      window.location.reload(false);
    });
  };
  const handleDelete = (id) => {
    console.log(23);
  };
  useEffect(() => {
    fetch("http://localhost:8082/ShoppingCartAdmin/user/getAll")
      .then((res) => res.json())
      .then((result) => {
        setUsers(result);
      });
  }, []);
  console.log(users);
  return (
    <>
      <div>
        <h2 class="text-center">Manage Users - View All</h2>
      </div>
      <div className="full-details">
        <table className="table table-bordered table-striped table-hover table-reponsive-xl">
          <thead className="thead-dark">
            <tr>
              <th className="hideable-column">User ID</th>
              <th>Photos</th>
              <th className="hideable-column">E-mail</th>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Roles</th>
              <th>Enabled</th>
              <th>
                <span>Edit</span>
                &nbsp; &nbsp; &nbsp;
                <span>Delete</span>
              </th>
            </tr>
          </thead>
          <tbody>
            {users.map((user, index) => {
              return (
                <tr key={index}>
                  <td className="hideable-column">{user.id}</td>
                  <td>
                    <span className="fa fa-portait fa-3x icon-silver">
                      <RxAvatar></RxAvatar>
                    </span>
                    {/* <img
                    th:if="${user.photos != null}"
                    th:src="@{${user.photosImagePath}}"
                    alt=""
                    style="width: 100px;"
                  /> */}
                  </td>
                  <td className="hideable-column">{user.email}</td>
                  <td>{user.firstName}</td>
                  <td>{user.lastName}</td>
                  <td>
                    {user.roles.map((role, index) => {
                      return <div key={index}>{role.name}</div>;
                    })}
                  </td>
                  <td>
                    <div>
                      {user.enabled ? (
                        <span
                          onClick={() => {
                            handleEnable(user.id, false);
                          }}
                          tittle="'Disable this ' + 'user'"
                          className="fas fa-check-circle fa-2x icon-green"
                        >
                          <FontAwesomeIcon icon={faCheckCircle} />
                        </span>
                      ) : (
                        <span
                          onClick={() => {
                            handleEnable(user.id, true);
                          }}
                          //   th:if="${user.enabled == false}"
                          //   th:href="@{'/users/' + ${user.id} + '/enabled/true'}"
                          //   th:tittle="'Enabled this ' + 'user'"
                          className="fas fa-circle fa-2x icon-dark"
                        >
                          <FontAwesomeIcon icon={faCheckCircle} color="grey" />
                        </span>
                      )}
                    </div>
                  </td>
                  <td>
                    <a
                      // th:href="@{'/user/edit/' + ${user.id}}"
                      // th:tittle="'Edit this' + 'user'"
                      className="fas fa-edit fa-2x icon-green"
                    >
                      <FontAwesomeIcon icon={faFilePen} color="grey" />
                    </a>
                    &nbsp;&nbsp;
                    <span
                      onCLick={handleDelete(user.id)}
                      // th:href="@{'/user/delete/' + ${user.id}}"
                      // th:entityId="${user.id}"
                      // th:tittle="'Delete this' + 'user'"
                      className="fas fa-trash fa-2x icon-dark link-delete"
                    >
                      <FontAwesomeIcon icon={faXmark} color="grey" />
                    </span>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </>
  );
};
