import React from "react";
import { useState, useEffect } from "react";
import avatar from "./../images/default-user.png";
import { useParams } from "react-router-dom";
import { toHaveFormValues } from "@testing-library/jest-dom";
import { HiBars3BottomLeft } from "react-icons/hi2";
// import { useNavigate } from "react-router-dom";
export const CategoryForm = (props) => {
  const params = useParams();
  const [name, setName] = useState("");
  const [alias, setAlias] = useState("");
  const [enabled, setEnabled] = useState(null);
  const [category, setCategory] = useState();
  // const history = useNavigate();
  const saveCategory = (e) => {
    e.preventDefault();
    const id = params.id ? params.id : null;
    const newCategory = {
      id,
      name,
      alias,
      enabled,
    };
    console.log(newCategory);
    fetch("http://localhost:8082/ShoppingCartAdmin/categories/save", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(newCategory),
    }).then(() => {
      console.log("Category saved");
      window.location.href = "/categories";
    });
  };

  useEffect(() => {
    if (params.id) {
      fetch("http://localhost:8082/ShoppingCartAdmin/category/get/" + params.id)
        .then((res) => res.json())
        .then((result) => {
          console.log(result);
          // setUser(result);
          // setEmail(result.email);
          // setFirstName(result.firstName);
          // setLastName(result.lastName);
          // setEnabled(result.enabled);
          // setPassword(result.password);
          // setUserRoles(result.roles);
        });
    }
  }, []);
  return (
    <>
      <div>
        <h2 className="text-center">Manage Category</h2>
      </div>
      <form
        action=""
        method="post"
        style={{ maxWidth: "700px", margin: "0 auto" }}
        //   style="max-wid 700px; margin: 0 auto;"
        //   object="${user}"
        //   enctype="multipart/form-data"
        //   onsubmit="return checkEmailUnique(this);"
      >
        <input type="hidden" />
        <div className="border border-secondary rounded p-3">
          <div className="form-group row">
            <label htmlFor="" className="col-sm-4 col-form-label">
              E-mail:
            </label>
            <div className="col-sm-8">
              <input
                type="email"
                className="form-control"
                required
                minLength="8"
                maxLength="128"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>
          </div>

          <div className="form-group row">
            <label htmlFor="" className="col-sm-4 col-form-label">
              First Name:
            </label>
            <div className="col-sm-8">
              <input
                type="text"
                className="form-control"
                required
                minLength="2"
                maxLength="45"
                value={firstName}
                onChange={(e) => setFirstName(e.target.value)}
              />
            </div>
          </div>

          <div className="form-group row">
            <label htmlFor="" className="col-sm-4 col-form-label">
              Last Name:
            </label>
            <div className="col-sm-8">
              <input
                type="text"
                className="form-control"
                required
                minLength="2"
                maxLength="45"
                value={lastName}
                onChange={(e) => setLastName(e.target.value)}
              />
            </div>
          </div>
          <div className="form-group row">
            <label htmlFor="" className="col-sm-4 col-form-label">
              Password:
            </label>
            <div className="col-sm-8">
              <input
                type="password"
                className="form-control"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
          </div>

          <div className="form-group row">
            <label className="col-sm-4 col-form-label">Roles: </label>
            <div className="col-sm-8">
              {Roles.map((role, index) => {
                return (
                  <div key={index}>
                    <input
                      type="checkbox"
                      text={role.name}
                      value={role.id}
                      checked={hasRole(role)}
                      onChange={(event) => handleRoleChange(role, event)}
                      className="m-2"
                    />
                    - <small>{role.description}</small>
                    <br />
                  </div>
                );
              })}
            </div>
          </div>

          <div className="form-group row">
            <label htmlFor="" className="col-sm-4 col-form label">
              Enabled:
            </label>
            <div className="col-sm-8">
              <input
                type="checkbox"
                defaultChecked={enabled}
                onChange={(e) => setEnabled(!enabled)}
              />
            </div>
          </div>

          <div className="form-group row">
            <label htmlFor="" className="col-sm-4 col-form-label">
              Photos:{" "}
            </label>
            <div className="col-sm-8">
              {/* <input type="hidden" field="*{photos}" /> */}
              <input
                type="file"
                id="fileImage"
                name="image"
                accept="image/png, image/jpg"
                className="mb-2"
              />
              <img
                src={avatar}
                alt="Photos Review"
                id="thumbmail"
                className="img-fluid"
              />
            </div>
          </div>
          <div className="text-center">
            <input
              type="submit"
              value="Save"
              className="btn btn-primary m-3"
              onClick={(e) => {
                saveUser(e);
              }}
            />
            <input
              type="button"
              value="Cancel"
              className="btn btn-secondary"
              id="buttonCancel"
            />
          </div>
        </div>
      </form>
    </>
  );
};
