import { useState, useEffect } from "react";
import React from "react";
import { FaDove, RxAvatar } from "react-icons/all";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faCheckCircle,
  faFilePen,
  faXmark,
  faSortAlphaAsc,
} from "@fortawesome/free-solid-svg-icons";
import { useLocation } from "react-router-dom";
import { useNavigate } from "react-router-dom";
export const Users = () => {
  const [users, setUsers] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const userPerPage = 4;
  const totalPage =
    users.length / userPerPage - Math.floor(users.length / userPerPage) == 0
      ? users.length / userPerPage
      : Math.floor(users.length / userPerPage) + 1;
  const { state } = useLocation();
  const [message, setMessage] = useState(
    state && state.message ? state.message.message : ""
  );
  const navigate = useNavigate();
  const [isAsc, setIsAsc] = useState(true);
  const [sortField, setSortField] = useState("");
  //Enable users
  const handleEnable = (id, bool) => {
    fetch(
      "http://localhost:8082/ShoppingCartAdmin/users/" +
        id +
        "/enabled/" +
        bool,
      {
        method: "GET",
        headers: { "Content-Type": "application/json" },
      }
    )
      .then((res) => res.json())
      .then((result) => setMessage(result.message))
      .then(() => {
        updateUsers();
      });
  };

  //Delete users
  const handleDelete = (id) => {
    fetch("http://localhost:8082/ShoppingCartAdmin/user/delete/" + id, {
      method: "GET",
      headers: { "Content-Type": "application/json" },
    })
      .then((res) => res.json())
      .then((result) => {
        setMessage(result.message);
      })
      .then(updateUsers);
  };

  //Update users after actions
  const updateUsers = () => {
    fetch("http://localhost:8082/ShoppingCartAdmin/user/getAll")
      .then((res) => res.json())
      .then((result) => {
        setUsers(result);
      });
  };

  //Load the users
  useEffect(() => {
    fetch("http://localhost:8082/ShoppingCartAdmin/user/getAll")
      .then((res) => res.json())
      .then((result) => {
        setUsers(result);
      });
    navigate(window.location.pathname, {});
  }, []);
  return (
    <>
      <div>
        <h2 className="text-center">Manage Users - View All</h2>
      </div>
      {message != "" ? (
        <div className="alert alert-success text-center">{message}</div>
      ) : null}

      <div className="full-details">
        <table className="table table-bordered table-striped table-hover table-reponsive-xl">
          <thead className="thead-dark">
            <tr>
              <th className="hideable-column">User ID</th>
              <th>Photos</th>
              <th className="hideable-column">E-mail</th>
              <th>
                <a
                  className="text-white"
                  onClick={() => {
                    if (isAsc) {
                      setIsAsc(!isAsc);
                      setSortField("firstName");
                      setUsers(
                        [...users].sort((a, b) =>
                          a.firstName > b.firstName ? 1 : -1
                        )
                      );
                    } else {
                      setIsAsc(!isAsc);
                      setSortField("firstName");
                      setUsers(
                        [...users].sort((a, b) =>
                          a.firstName > b.firstName ? -1 : 1
                        )
                      );
                    }
                  }}
                >
                  First Name
                </a>
                {/* {sortField == "firstName" ? ( */}
                <span
                // className={isAsc ? "bi bi-sort-up" : "fbi bi-sort-down"}
                >
                  {faSortAlphaAsc}
                </span>
                {/* ) : null} */}
              </th>
              <th>Last Name</th>
              <th>Roles</th>
              <th>Enabled</th>
              <th>
                <span>Edit</span>
                &nbsp; &nbsp; &nbsp;
                <span>Delete</span>
              </th>
            </tr>
          </thead>
          <tbody>
            {users
              .slice(
                (currentPage - 1) * userPerPage,
                currentPage * userPerPage < users.length
                  ? currentPage * userPerPage
                  : users.length
              )
              .map((user, index) => {
                return (
                  <tr key={index}>
                    <td className="hideable-column">{user.id}</td>
                    <td>
                      <span className="fa fa-portait fa-3x icon-silver">
                        <RxAvatar></RxAvatar>
                      </span>
                      {/* <img
                    th:if="${user.photos != null}"
                    th:src="@{${user.photosImagePath}}"
                    alt=""
                    style="width: 100px;"
                  /> */}
                    </td>
                    <td className="hideable-column">{user.email}</td>
                    <td>{user.firstName}</td>
                    <td>{user.lastName}</td>
                    <td>
                      {user.roles.map((role, index) => {
                        return <div key={index}>{role.name}</div>;
                      })}
                    </td>
                    <td>
                      <div>
                        {user.enabled ? (
                          <span
                            onClick={() => {
                              handleEnable(user.id, false);
                            }}
                            tittle="'Disable this ' + 'user'"
                            className="fas fa-check-circle fa-2x icon-green"
                          >
                            <FontAwesomeIcon icon={faCheckCircle} />
                          </span>
                        ) : (
                          <span
                            onClick={() => {
                              handleEnable(user.id, true);
                            }}
                            //   th:if="${user.enabled == false}"
                            //   th:href="@{'/users/' + ${user.id} + '/enabled/true'}"
                            //   th:tittle="'Enabled this ' + 'user'"
                            className="fas fa-circle fa-2x icon-dark"
                          >
                            <FontAwesomeIcon
                              icon={faCheckCircle}
                              color="grey"
                            />
                          </span>
                        )}
                      </div>
                    </td>
                    <td>
                      <a
                        href={"/user/update/" + user.id}
                        // th:tittle="'Edit this' + 'user'"
                        className="fas fa-edit fa-2x icon-green"
                      >
                        <FontAwesomeIcon icon={faFilePen} color="grey" />
                      </a>
                      &nbsp;&nbsp;
                      <span
                        onClick={() => {
                          handleDelete(user.id);
                        }}
                        // th:href="@{'/user/delete/' + ${user.id}}"
                        // th:entityId="${user.id}"
                        // th:tittle="'Delete this' + 'user'"
                        className="fas fa-trash fa-2x icon-dark link-delete"
                      >
                        <FontAwesomeIcon icon={faXmark} color="grey" />
                      </span>
                    </td>
                  </tr>
                );
              })}
          </tbody>
        </table>
      </div>
      <nav>
        <ul className="pagination justify-content-center flex-wrap">
          <li className={currentPage > 1 ? "page-item" : "page-item disabled"}>
            <button
              onClick={() => {
                setCurrentPage(1);
              }}
              className="page-link"
            >
              First
            </button>
          </li>
          <li className={currentPage > 1 ? "page-item" : "page-item disabled"}>
            <button
              onClick={() => {
                if (currentPage > 1) {
                  setCurrentPage(currentPage - 1);
                }
              }}
              className="page-link"
            >
              Previous
            </button>
          </li>
          {Array(totalPage)
            .fill(null)
            .map((item, index) => {
              if (index + 1 == currentPage) {
                return (
                  <div key={index} className="page-item active">
                    <a href="" className="page-link">
                      {index + 1}
                    </a>
                  </div>
                );
              } else {
                return (
                  <div key={index} className="page-item">
                    <button
                      onClick={() => {
                        setCurrentPage(index + 1);
                      }}
                      className="page-link"
                    >
                      {index + 1}
                    </button>
                  </div>
                );
              }
            })}
          <li
            className={
              currentPage < totalPage ? "page-item" : "page-item disabled"
            }
          >
            <button
              onClick={() => {
                if (currentPage < totalPage) {
                  setCurrentPage(currentPage + 1);
                }
              }}
              className="page-link"
            >
              Next
            </button>
          </li>

          <li
            className={
              currentPage < totalPage ? "page-item" : "page-item disabled"
            }
          >
            <button
              onClick={() => {
                setCurrentPage(totalPage);
              }}
              className="page-link"
            >
              Last
            </button>
          </li>
        </ul>
      </nav>
    </>
  );
};
