import React from "react";
import { useState, useEffect } from "react";
import avatar from "./../images/default-user.png";
import { useParams } from "react-router-dom";
import { toHaveFormValues } from "@testing-library/jest-dom";
import { HiBars3BottomLeft } from "react-icons/hi2";
import { useNavigate } from "react-router-dom";
// import { useNavigate } from "react-router-dom";
export const CategoryForm = (props) => {
  const params = useParams();
  const [name, setName] = useState("");
  const [alias, setAlias] = useState("");
  const [enabled, setEnabled] = useState(null);
  const [category, setCategory] = useState();
  const navigate = useNavigate();
  // const history = useNavigate();
  const saveCategory = (e) => {
    e.preventDefault();
    const id = params.id ? params.id : null;
    const newCategory = {
      id,
      name,
      alias,
      enabled,
    };
    fetch("http://localhost:8082/ShoppingCartAdmin/categories/save", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(newCategory),
    })
      .then((res) => res.json())
      .then((result) => {
        console.log(result);
        // navigate("/categories", { state: { message: result } });
      })
      .then(() => {
        console.log("Category saved");
      });
  };

  useEffect(() => {
    if (params.id) {
      fetch(
        "http://localhost:8082/ShoppingCartAdmin/category/get/" + params.id,
        {
          headers: {
            "Content-Type": "application/json",
            Accept: "application/json",
          },
        }
      )
        .then((res) => res.json())
        .then((result) => {
          console.log(result);
          setName(result.name);
          setAlias(result.alias);
          setEnabled(result.enabled);
        });
    }
  }, []);
  return (
    <>
      <div>
        <h2 className="text-center">Manage Category</h2>
      </div>
      <form
        action=""
        method="post"
        style={{ maxWidth: "700px", margin: "0 auto" }}
        //   style="max-wid 700px; margin: 0 auto;"
        //   object="${user}"
        //   enctype="multipart/form-data"
        //   onsubmit="return checkEmailUnique(this);"
      >
        <div className="border border-secondary rounded p-3">
          <div className="form-group row">
            <label htmlFor="" className="col-sm-4 col-form-label">
              Name:
            </label>
            <div className="col-sm-8">
              <input
                type="text"
                className="form-control"
                required
                minLength="8"
                maxLength="128"
                value={name}
                onChange={(e) => setName(e.target.value)}
              />
            </div>
          </div>

          <div className="form-group row">
            <label htmlFor="" className="col-sm-4 col-form-label">
              Alias:
            </label>
            <div className="col-sm-8">
              <input
                type="text"
                className="form-control"
                required
                minLength="2"
                maxLength="45"
                value={alias}
                onChange={(e) => setAlias(e.target.value)}
              />
            </div>
          </div>

          <div className="form-group row">
            <label htmlFor="" className="col-sm-4 col-form label">
              Enabled:
            </label>
            <div className="col-sm-8">
              <input
                type="checkbox"
                defaultChecked={enabled}
                onChange={(e) => setEnabled(!enabled)}
              />
            </div>
          </div>

          <div className="form-group row">
            <label htmlFor="" className="col-sm-4 col-form-label">
              Photos:{" "}
            </label>
            <div className="col-sm-8">
              {/* <input type="hidden" field="*{photos}" /> */}
              <input
                type="file"
                id="fileImage"
                name="image"
                accept="image/png, image/jpg"
                className="mb-2"
              />
              <img
                src={avatar}
                alt="Photos Review"
                id="thumbmail"
                className="img-fluid"
              />
            </div>
          </div>
          <div className="text-center">
            <input
              type="submit"
              value="Save"
              className="btn btn-primary m-3"
              onClick={(e) => {
                saveCategory(e);
              }}
            />
            <input
              type="button"
              value="Cancel"
              className="btn btn-secondary"
              id="buttonCancel"
            />
          </div>
        </div>
      </form>
    </>
  );
};
